import React from 'react';
import { motion } from 'framer-motion';

const AnimatedBackground = ({ variant = 'default' }) => {
  // Floating particles
  const particles = Array.from({ length: 30 }, (_, i) => ({
    id: i,
    size: Math.random() * 6 + 2,
    x: Math.random() * 100,
    y: Math.random() * 100,
    duration: Math.random() * 25 + 20,
    delay: Math.random() * 5,
  }));

  // Moving lines
  const lines = Array.from({ length: 12 }, (_, i) => ({
    id: i,
    x: Math.random() * 100,
    y: Math.random() * 100,
    rotation: Math.random() * 360,
    duration: Math.random() * 20 + 15,
  }));

  // Floating shapes
  const shapes = Array.from({ length: 8 }, (_, i) => ({
    id: i,
    size: Math.random() * 150 + 80,
    x: Math.random() * 100,
    y: Math.random() * 100,
    duration: Math.random() * 30 + 25,
  }));

  // Wave patterns
  const waves = Array.from({ length: 3 }, (_, i) => ({
    id: i,
    amplitude: 50 + i * 30,
    frequency: 0.5 + i * 0.3,
    speed: 2 + i * 1,
  }));

  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {/* Animated Gradient Background - Video-like effect */}
      <motion.div
        className="absolute inset-0 opacity-30"
        animate={{
          background: [
            'radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 80% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 50% 20%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 50% 80%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
          ],
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: 'easeInOut',
        }}
      />

      {/* Animated Wave Patterns */}
      {waves.map((wave) => (
        <motion.svg
          key={`wave-${wave.id}`}
          className="absolute inset-0 w-full h-full"
          style={{ opacity: 0.05 }}
          animate={{
            x: [0, 100, 0],
            y: [0, 50, 0],
          }}
          transition={{
            duration: wave.speed * 10,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        >
          <motion.path
            d={`M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`}
            fill="none"
            stroke="white"
            strokeWidth="1"
            animate={{
              d: [
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 + wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
              ],
            }}
            transition={{
              duration: wave.speed * 3,
              repeat: Infinity,
              ease: 'easeInOut',
            }}
          />
        </motion.svg>
      ))}

      {/* Animated Mesh Gradient - Video-like */}
      <motion.div
        className="absolute inset-0 opacity-20"
        animate={{
          background: [
            'radial-gradient(ellipse at top left, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom right, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at top right, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom left, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at center, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at top, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at top left, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom right, rgba(255,255,255,0.15) 0%, transparent 50%)',
          ],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: 'easeInOut',
        }}
      />
      {/* Floating Particles */}
      {particles.map((particle) => (
        <motion.div
          key={`particle-${particle.id}`}
          className="absolute rounded-full bg-white/10"
          style={{
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            left: `${particle.x}%`,
            top: `${particle.y}%`,
          }}
          animate={{
            y: [
              particle.y * 10,
              (particle.y * 10) + Math.random() * 200 - 100,
              particle.y * 10,
            ],
            x: [
              particle.x * 10,
              (particle.x * 10) + Math.random() * 200 - 100,
              particle.x * 10,
            ],
            opacity: [0.1, 0.3, 0.1],
            scale: [1, 1.5, 1],
          }}
          transition={{
            duration: particle.duration,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: particle.delay,
          }}
        />
      ))}

      {/* Moving Lines */}
      {lines.map((line) => (
        <motion.div
          key={`line-${line.id}`}
          className="absolute w-px h-32 bg-white/5"
          style={{
            left: `${line.x}%`,
            top: `${line.y}%`,
            transformOrigin: 'center',
          }}
          animate={{
            y: [
              line.y * 10,
              (line.y * 10) + Math.random() * 300 - 150,
              line.y * 10,
            ],
            x: [
              line.x * 10,
              (line.x * 10) + Math.random() * 200 - 100,
              line.x * 10,
            ],
            rotate: [line.rotation, line.rotation + 360, line.rotation],
            opacity: [0.05, 0.15, 0.05],
          }}
          transition={{
            duration: line.duration,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      ))}

      {/* Floating Geometric Shapes */}
      {shapes.map((shape) => (
        <motion.div
          key={`shape-${shape.id}`}
          className="absolute border border-white/5 rounded-lg"
          style={{
            width: `${shape.size}px`,
            height: `${shape.size}px`,
            left: `${shape.x}%`,
            top: `${shape.y}%`,
          }}
          animate={{
            y: [
              shape.y * 10,
              (shape.y * 10) + Math.random() * 400 - 200,
              shape.y * 10,
            ],
            x: [
              shape.x * 10,
              (shape.x * 10) + Math.random() * 300 - 150,
              shape.x * 10,
            ],
            rotate: [0, 360],
            scale: [1, 1.2, 1],
            opacity: [0.03, 0.08, 0.03],
          }}
          transition={{
            duration: shape.duration,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      ))}

      {/* Animated Circles */}
      {Array.from({ length: 4 }).map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="absolute rounded-full border border-white/5"
          style={{
            width: `${100 + i * 50}px`,
            height: `${100 + i * 50}px`,
            left: `${20 + i * 20}%`,
            top: `${10 + i * 15}%`,
          }}
          animate={{
            scale: [1, 1.3, 1],
            opacity: [0.05, 0.1, 0.05],
            rotate: [0, 180, 360],
          }}
          transition={{
            duration: 20 + i * 5,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      ))}

      {/* Moving Dots Pattern */}
      {Array.from({ length: 15 }).map((_, i) => (
        <motion.div
          key={`dot-${i}`}
          className="absolute w-1 h-1 bg-white/20 rounded-full"
          style={{
            left: `${(i * 7) % 100}%`,
            top: `${(i * 11) % 100}%`,
          }}
          animate={{
            y: [
              0,
              Math.sin(i) * 100,
              0,
            ],
            x: [
              0,
              Math.cos(i) * 100,
              0,
            ],
            opacity: [0.1, 0.4, 0.1],
          }}
          transition={{
            duration: 10 + i * 2,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: i * 0.5,
          }}
        />
      ))}
    </div>
  );
};

export default AnimatedBackground;
