import React, { useRef, useState, useEffect } from 'react';
import { motion, useScroll, useTransform, AnimatePresence } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import AnimatedBackground from '../components/AnimatedBackground';

const Projects = () => {
  const ref = useRef(null);
  const [hasBeenViewed, setHasBeenViewed] = useState(false);
  const [isLeaving, setIsLeaving] = useState(false);
  const [isEntering, setIsEntering] = useState(false);

  const [inViewRef, inView] = useInView({
    threshold: 0.2,
    triggerOnce: false,
  });

  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ['start end', 'end start'],
  });

  const backgroundY = useTransform(scrollYProgress, [0, 1], ['0%', '20%']);

  // Transform effects
  const enterY = useTransform(scrollYProgress, [0, 0.3], ['100%', '0%']);
  const enterOpacity = useTransform(scrollYProgress, [0, 0.3], [0, 1]);
  const leaveY = useTransform(scrollYProgress, [0.7, 1], ['0%', '-100%']);
  const leaveOpacity = useTransform(scrollYProgress, [0.7, 1], [1, 0]);
  const leaveScale = useTransform(scrollYProgress, [0.7, 1], [1, 0.8]);

  useEffect(() => {
    if (inView && !hasBeenViewed) {
      setIsEntering(true);
      setTimeout(() => {
        setIsEntering(false);
        setHasBeenViewed(true);
      }, 1000);
    } else if (!inView && hasBeenViewed) {
      setIsLeaving(true);
      setTimeout(() => {
        setIsLeaving(false);
      }, 800);
    }
  }, [inView, hasBeenViewed]);

  const setRefs = (node) => {
    ref.current = node;
    inViewRef(node);
  };

  const projects = [
    {
      id: 1,
      title: 'E-Commerce Platform',
      category: 'Web Design',
      description:
        'A modern e-commerce platform with intuitive user experience and seamless checkout process.',
      tags: ['UI/UX', 'Figma', 'Prototyping'],
    },
    {
      id: 2,
      title: 'Mobile Banking App',
      category: 'Mobile Design',
      description:
        'Secure and user-friendly mobile banking application with advanced security features.',
      tags: ['Mobile', 'iOS', 'Android'],
    },
    {
      id: 3,
      title: 'SaaS Dashboard',
      category: 'Web Design',
      description:
        'Comprehensive dashboard design for SaaS platform with data visualization and analytics.',
      tags: ['Dashboard', 'Data Viz', 'Web'],
    },
    {
      id: 4,
      title: 'Fitness App',
      category: 'Mobile Design',
      description:
        'Motivational fitness tracking app with beautiful UI and engaging user experience.',
      tags: ['Mobile', 'Health', 'Fitness'],
    },
    {
      id: 5,
      title: 'Restaurant Website',
      category: 'Web Design',
      description:
        'Elegant restaurant website with online ordering system and menu showcase.',
      tags: ['Web', 'Food', 'E-commerce'],
    },
    {
      id: 6,
      title: 'Travel Booking App',
      category: 'Mobile Design',
      description:
        'Streamlined travel booking experience with easy navigation and booking flow.',
      tags: ['Mobile', 'Travel', 'Booking'],
    },
  ];

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.2,
        duration: 0.8,
      },
    },
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 100,
      scale: 0.85,
      rotateX: 15,
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      rotateX: 0,
      transition: {
        duration: 0.8,
        ease: [0.6, -0.05, 0.01, 0.99],
      },
    },
  };

  const titleVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      scale: 0.95,
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.8,
        ease: [0.6, -0.05, 0.01, 0.99],
      },
    },
  };

  return (
    <section
      id="projects"
      ref={setRefs}
      className="relative py-20 md:py-32 px-4 sm:px-6 lg:px-8 bg-black overflow-hidden transition-all duration-1000"
    >
      {/* Background Pattern with Parallax */}
      <motion.div
        style={{ y: backgroundY }}
        className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:50px_50px]"
      />

      {/* Animated Background Elements */}
      <AnimatedBackground variant="projects" />

      <div className="max-w-7xl mx-auto relative z-10">
        <AnimatePresence mode="wait">
          {(!hasBeenViewed || inView) && (
            <motion.div
              key="projects-content"
              initial="hidden"
              animate={inView ? 'visible' : 'hidden'}
              exit={{ 
                opacity: 0, 
                y: -100, 
                scale: 0.8,
                transition: { duration: 0.8 }
              }}
              style={{
                y: isEntering ? enterY : isLeaving ? leaveY : 0,
                opacity: isEntering ? enterOpacity : isLeaving ? leaveOpacity : 1,
                scale: isLeaving ? leaveScale : 1,
              }}
            >
              <motion.div
                variants={containerVariants}
                initial="hidden"
                animate={inView ? 'visible' : 'hidden'}
              >
        {/* Section Title */}
        <motion.div
          variants={titleVariants}
          initial="hidden"
          animate={inView ? 'visible' : 'hidden'}
          className="text-center mb-16"
        >
          <motion.span
            className="inline-block text-xs font-extralight text-white/40 uppercase tracking-[0.3em] mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ delay: 0.1, duration: 0.8 }}
          >
            Portfolio
          </motion.span>
          <motion.h2
            className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-extralight text-white leading-[1.1] tracking-[-0.02em]"
            initial={{ opacity: 0, y: 60, scale: 0.95 }}
            animate={inView ? { opacity: 1, y: 0, scale: 1 } : {}}
            transition={{ delay: 0.2, duration: 1, ease: [0.6, -0.05, 0.01, 0.99] }}
          >
            Featured
            <span className="block bg-gradient-to-r from-white via-white/90 to-white/70 bg-clip-text text-transparent font-extralight">
              Projects
            </span>
          </motion.h2>
        </motion.div>

        {/* Projects Grid - Asymmetric Masonry Layout */}
        <motion.div
          className="grid md:grid-cols-2 lg:grid-cols-3 gap-6 lg:gap-8 auto-rows-fr"
        >
          {projects.map((project, index) => {
            // Create asymmetric layout - some cards taller
            const isTall = index === 1 || index === 4;
            const isWide = index === 2;

            return (
            <motion.div
              key={project.id}
              variants={itemVariants}
              className={`group relative bg-white/5 rounded-3xl overflow-hidden border border-white/10 backdrop-blur-xl cursor-pointer ${
                isTall ? 'md:row-span-2' : ''
              } ${isWide ? 'md:col-span-2 lg:col-span-1' : ''}`}
              whileHover={{ 
                y: -25,
                scale: 1.05,
                borderColor: 'rgba(255, 255, 255, 0.5)',
                boxShadow: '0 40px 100px rgba(255, 255, 255, 0.25), inset 0 0 30px rgba(255, 255, 255, 0.05)',
                backgroundColor: 'rgba(255, 255, 255, 0.1)',
              }}
              transition={{ 
                type: 'spring',
                stiffness: 300,
                damping: 25,
              }}
            >
              {/* Project Image Placeholder */}
              <div className={`relative bg-gradient-to-br from-white/10 to-white/5 overflow-hidden ${
                isTall ? 'h-96' : 'h-64'
              }`}>
