import React, { useEffect, useState } from 'react';
import { motion, useScroll, useSpring, useTransform } from 'framer-motion';

const ScrollProgress = () => {
  const { scrollYProgress } = useScroll();
  const scaleX = useSpring(scrollYProgress, {
    stiffness: 100,
    damping: 30,
    restDelta: 0.001,
  });

  const [sections, setSections] = useState([]);

  useEffect(() => {
    const sectionIds = ['home', 'about', 'skills', 'projects', 'contact'];
    const sectionElements = sectionIds.map(id => {
      const element = document.getElementById(id);
      if (element) {
        const rect = element.getBoundingClientRect();
        const scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        return {
          id,
          top: rect.top + scrollTop,
          height: rect.height,
        };
      }
      return null;
    }).filter(Boolean);
    setSections(sectionElements);
  }, []);

  const opacity = useTransform(scrollYProgress, [0, 0.1, 0.9, 1], [0, 1, 1, 0]);

  return (
    <motion.div
      className="fixed top-0 left-0 right-0 h-1 bg-white/10 z-50 origin-left"
      style={{ opacity }}
    >
      {/* Main Progress Bar */}
      <motion.div
        className="h-full bg-gradient-to-r from-white/30 via-white to-white/30 origin-left"
        style={{ scaleX }}
      >
        {/* Glow Effect */}
        <motion.div
          className="absolute inset-0 bg-white blur-sm"
          style={{ scaleX, opacity: 0.5 }}
        />
      </motion.div>

      {/* Section Markers */}
      {sections.length > 0 && (
        <div className="absolute inset-0 flex">
          {sections.map((section, index) => {
            const sectionProgress = useTransform(
              scrollYProgress,
              [0, 1],
              [0, 1]
            );

            return (
              <motion.div
                key={section.id}
                className="absolute top-0 w-1 h-full bg-white/30"
                style={{
                  left: `${(index / (sections.length - 1)) * 100}%`,
                }}
                animate={{
                  opacity: [0.2, 0.6, 0.2],
                }}
                transition={{
                  duration: 2,
                  repeat: Infinity,
                  ease: 'easeInOut',
                  delay: index * 0.3,
                }}
              />
            );
          })}
        </div>
      )}
    </motion.div>
  );
};

export default ScrollProgress;
