import React from 'react';
import { motion } from 'framer-motion';

const AnimatedBackground = ({ variant = 'default' }) => {
  // Floating particles
  const particles = Array.from({ length: 30 }, (_, i) => ({
    id: i,
    size: Math.random() * 6 + 2,
    x: Math.random() * 100,
    y: Math.random() * 100,
    duration: Math.random() * 25 + 20,
    delay: Math.random() * 5,
  }));

  // Moving lines
  const lines = Array.from({ length: 12 }, (_, i) => ({
    id: i,
    x: Math.random() * 100,
    y: Math.random() * 100,
    rotation: Math.random() * 360,
    duration: Math.random() * 20 + 15,
  }));

  // Floating shapes
  const shapes = Array.from({ length: 8 }, (_, i) => ({
    id: i,
    size: Math.random() * 150 + 80,
    x: Math.random() * 100,
    y: Math.random() * 100,
    duration: Math.random() * 30 + 25,
  }));

  // Wave patterns
  const waves = Array.from({ length: 3 }, (_, i) => ({
    id: i,
    amplitude: 50 + i * 30,
    frequency: 0.5 + i * 0.3,
    speed: 2 + i * 1,
  }));

  return (
    <div className="absolute inset-0 overflow-hidden pointer-events-none">
      {/* Animated Gradient Background - Video-like effect */}
      <motion.div
        className="absolute inset-0 opacity-30"
        animate={{
          background: [
            'radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 80% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 50% 20%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 50% 80%, rgba(255,255,255,0.1) 0%, transparent 50%)',
            'radial-gradient(circle at 20% 50%, rgba(255,255,255,0.1) 0%, transparent 50%)',
          ],
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: 'easeInOut',
        }}
      />

      {/* Animated Wave Patterns */}
      {waves.map((wave) => (
        <motion.svg
          key={`wave-${wave.id}`}
          className="absolute inset-0 w-full h-full"
          style={{ opacity: 0.05 }}
          animate={{
            x: [0, 100, 0],
            y: [0, 50, 0],
          }}
          transition={{
            duration: wave.speed * 10,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        >
          <motion.path
            d={`M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`}
            fill="none"
            stroke="white"
            strokeWidth="1"
            animate={{
              d: [
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 + wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
                `M 0 ${200 + wave.id * 100} Q ${wave.amplitude} ${200 + wave.id * 100 - wave.amplitude}, ${wave.amplitude * 2} ${200 + wave.id * 100} T ${wave.amplitude * 4} ${200 + wave.id * 100}`,
              ],
            }}
            transition={{
              duration: wave.speed * 3,
              repeat: Infinity,
              ease: 'easeInOut',
            }}
          />
        </motion.svg>
      ))}

      {/* Animated Mesh Gradient - Video-like */}
      <motion.div
        className="absolute inset-0 opacity-20"
        animate={{
          background: [
            'radial-gradient(ellipse at top left, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom right, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at top right, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom left, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at center, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at top, rgba(255,255,255,0.15) 0%, transparent 50%)',
            'radial-gradient(ellipse at top left, rgba(255,255,255,0.15) 0%, transparent 50%), radial-gradient(ellipse at bottom right, rgba(255,255,255,0.15) 0%, transparent 50%)',
          ],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: 'easeInOut',
        }}
      />
      {/* Floating Particles with Video-like Motion */}
      {particles.map((particle) => (
        <motion.div
          key={`particle-${particle.id}`}
          className="absolute rounded-full bg-white/15 blur-[1px]"
          style={{
            width: `${particle.size}px`,
            height: `${particle.size}px`,
            left: `${particle.x}%`,
            top: `${particle.y}%`,
          }}
          animate={{
            y: [
              particle.y * 10,
              (particle.y * 10) + Math.sin(particle.id) * 300,
              (particle.y * 10) + Math.cos(particle.id) * 300,
              particle.y * 10,
            ],
            x: [
              particle.x * 10,
              (particle.x * 10) + Math.cos(particle.id) * 300,
              (particle.x * 10) + Math.sin(particle.id) * 300,
              particle.x * 10,
            ],
            opacity: [0.05, 0.25, 0.15, 0.05],
            scale: [1, 1.8, 1.2, 1],
            rotate: [0, 360],
          }}
          transition={{
            duration: particle.duration,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: particle.delay,
          }}
        />
      ))}

      {/* Moving Lines with Smooth Transitions */}
      {lines.map((line) => (
        <motion.div
          key={`line-${line.id}`}
          className="absolute w-0.5 h-40 bg-gradient-to-b from-white/10 via-white/5 to-transparent blur-[0.5px]"
          style={{
            left: `${line.x}%`,
            top: `${line.y}%`,
            transformOrigin: 'center',
          }}
          animate={{
            y: [
              line.y * 10,
              (line.y * 10) + Math.sin(line.id) * 400,
              (line.y * 10) + Math.cos(line.id) * 400,
              line.y * 10,
            ],
            x: [
              line.x * 10,
              (line.x * 10) + Math.cos(line.id) * 300,
              (line.x * 10) + Math.sin(line.id) * 300,
              line.x * 10,
            ],
            rotate: [line.rotation, line.rotation + 720, line.rotation],
            opacity: [0.03, 0.12, 0.08, 0.03],
            scaleY: [1, 1.5, 0.8, 1],
          }}
          transition={{
            duration: line.duration,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      ))}

      {/* Floating Geometric Shapes with Video-like Motion */}
      {shapes.map((shape) => (
        <motion.div
          key={`shape-${shape.id}`}
          className="absolute border border-white/8 rounded-2xl backdrop-blur-sm"
          style={{
            width: `${shape.size}px`,
            height: `${shape.size}px`,
            left: `${shape.x}%`,
            top: `${shape.y}%`,
          }}
          animate={{
            y: [
              shape.y * 10,
              (shape.y * 10) + Math.sin(shape.id) * 500,
              (shape.y * 10) + Math.cos(shape.id) * 500,
              shape.y * 10,
            ],
            x: [
              shape.x * 10,
              (shape.x * 10) + Math.cos(shape.id) * 400,
              (shape.x * 10) + Math.sin(shape.id) * 400,
              shape.x * 10,
            ],
            rotate: [0, 180, 360],
            scale: [1, 1.3, 0.9, 1],
            opacity: [0.02, 0.1, 0.05, 0.02],
            borderRadius: ['8px', '50%', '8px'],
          }}
          transition={{
            duration: shape.duration,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
      ))}

      {/* Animated Circles with Pulsing Effect */}
      {Array.from({ length: 5 }).map((_, i) => (
        <motion.div
          key={`circle-${i}`}
          className="absolute rounded-full border border-white/8 backdrop-blur-sm"
          style={{
            width: `${150 + i * 80}px`,
            height: `${150 + i * 80}px`,
            left: `${15 + i * 18}%`,
            top: `${8 + i * 12}%`,
          }}
          animate={{
            scale: [1, 1.5, 0.8, 1],
            opacity: [0.03, 0.12, 0.06, 0.03],
            rotate: [0, 180, 360],
            x: [0, Math.sin(i) * 100, 0],
            y: [0, Math.cos(i) * 100, 0],
          }}
          transition={{
            duration: 25 + i * 8,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: i * 2,
          }}
        />
      ))}

      {/* Animated Gradient Orbs - Video-like */}
      {Array.from({ length: 3 }).map((_, i) => (
        <motion.div
          key={`orb-${i}`}
          className="absolute rounded-full blur-3xl"
          style={{
            width: `${200 + i * 100}px`,
            height: `${200 + i * 100}px`,
            background: `radial-gradient(circle, rgba(255,255,255,${0.1 - i * 0.02}) 0%, transparent 70%)`,
            left: `${20 + i * 30}%`,
            top: `${15 + i * 25}%`,
          }}
          animate={{
            x: [
              0,
              Math.sin(i * 2) * 200,
              Math.cos(i * 2) * 200,
              0,
            ],
            y: [
              0,
              Math.cos(i * 2) * 200,
              Math.sin(i * 2) * 200,
              0,
            ],
            scale: [1, 1.4, 0.7, 1],
            opacity: [0.05, 0.15, 0.08, 0.05],
          }}
          transition={{
            duration: 18 + i * 5,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: i * 3,
          }}
        />
      ))}

      {/* Moving Dots Pattern with Smooth Transitions */}
      {Array.from({ length: 20 }).map((_, i) => (
        <motion.div
          key={`dot-${i}`}
          className="absolute w-1.5 h-1.5 bg-white/25 rounded-full blur-[0.5px]"
          style={{
            left: `${(i * 5) % 100}%`,
            top: `${(i * 7) % 100}%`,
          }}
          animate={{
            y: [
              0,
              Math.sin(i * 0.5) * 150,
              Math.cos(i * 0.5) * 150,
              0,
            ],
            x: [
              0,
              Math.cos(i * 0.5) * 150,
              Math.sin(i * 0.5) * 150,
              0,
            ],
            opacity: [0.08, 0.35, 0.2, 0.08],
            scale: [1, 1.5, 0.8, 1],
          }}
          transition={{
            duration: 12 + i * 1.5,
            repeat: Infinity,
            ease: 'easeInOut',
            delay: i * 0.3,
          }}
        />
      ))}

      {/* Animated Grid Overlay - Video-like Transition */}
      <motion.div
        className="absolute inset-0 opacity-10"
        style={{
          backgroundImage: `
            linear-gradient(rgba(255,255,255,0.1) 1px, transparent 1px),
            linear-gradient(90deg, rgba(255,255,255,0.1) 1px, transparent 1px)
          `,
          backgroundSize: '50px 50px',
        }}
        animate={{
          backgroundPosition: ['0 0', '50px 50px', '0 0'],
          opacity: [0.05, 0.15, 0.05],
        }}
        transition={{
          duration: 20,
          repeat: Infinity,
          ease: 'linear',
        }}
      />

      {/* Flowing Light Effect */}
      <motion.div
        className="absolute inset-0"
        style={{
          background: 'linear-gradient(45deg, transparent 30%, rgba(255,255,255,0.05) 50%, transparent 70%)',
        }}
        animate={{
          x: ['-100%', '200%'],
          rotate: [0, 360],
        }}
        transition={{
          duration: 15,
          repeat: Infinity,
          ease: 'linear',
        }}
      />
    </div>
  );
};

export default AnimatedBackground;
