import React, { useRef } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import profileImage from '../assets/images/profile-placeholder.svg';
import AnimatedBackground from '../components/AnimatedBackground';

const About = () => {
  const ref = useRef(null);
  const [inViewRef, inView] = useInView({
    threshold: 0.1,
    triggerOnce: false,
  });

  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ['start end', 'end start'],
  });

  const imageY = useTransform(scrollYProgress, [0, 1], ['0%', '30%']);
  const imageScale = useTransform(scrollYProgress, [0, 0.5, 1], [1, 1.05, 1]);
  const imageRotate = useTransform(scrollYProgress, [0, 1], [0, 5]);

  const setRefs = (node) => {
    ref.current = node;
    inViewRef(node);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.15,
        delayChildren: 0.2,
        duration: 0.8,
      },
    },
  };

  const itemVariants = {
    hidden: { 
      opacity: 0, 
      y: 80,
      scale: 0.9,
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.8,
        ease: [0.6, -0.05, 0.01, 0.99],
      },
    },
  };

  const titleVariants = {
    hidden: { 
      opacity: 0, 
      y: 50,
      scale: 0.95,
    },
    visible: {
      opacity: 1,
      y: 0,
      scale: 1,
      transition: {
        duration: 0.8,
        ease: [0.6, -0.05, 0.01, 0.99],
      },
    },
  };

  const stats = [
    { number: '5+', label: 'Years Experience' },
    { number: '50+', label: 'Projects Completed' },
    { number: '30+', label: 'Happy Clients' },
    { number: '100%', label: 'Satisfaction Rate' },
  ];

  return (
    <section
      id="about"
      ref={setRefs}
      className="relative py-20 md:py-32 px-4 sm:px-6 lg:px-8 bg-black overflow-hidden"
    >
      {/* Background Pattern */}
      <div className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.02)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.02)_1px,transparent_1px)] bg-[size:50px_50px]" />

      {/* Animated Background Elements */}
      <AnimatedBackground variant="about" />

      <div className="max-w-7xl mx-auto relative z-10">
        <motion.div
          variants={containerVariants}
          initial="hidden"
          animate={inView ? 'visible' : 'hidden'}
        >
          {/* Section Title */}
          <motion.div variants={titleVariants} className="text-center mb-16" initial="hidden" animate={inView ? 'visible' : 'hidden'}>
            <motion.span
              className="inline-block text-xs font-light text-white/50 uppercase tracking-[0.2em] mb-6"
              initial={{ opacity: 0, y: 20 }}
              animate={inView ? { opacity: 1, y: 0 } : {}}
              transition={{ delay: 0.1, duration: 0.6 }}
            >
              About Me
            </motion.span>
            <motion.h2
              className="text-5xl sm:text-6xl md:text-7xl lg:text-8xl font-extralight text-white leading-tight tracking-tight"
              initial={{ opacity: 0, y: 50, scale: 0.9 }}
              animate={inView ? { opacity: 1, y: 0, scale: 1 } : {}}
              transition={{ delay: 0.2, duration: 0.8, ease: [0.6, -0.05, 0.01, 0.99] }}
            >
              Passionate About
              <span className="block bg-gradient-to-r from-white to-white/70 bg-clip-text text-transparent">
                Design
              </span>
            </motion.h2>
          </motion.div>

          {/* Content Grid */}
          <div className="grid md:grid-cols-2 gap-12 lg:gap-20 items-center mb-16">
            {/* Text Content */}
            <motion.div variants={itemVariants}>
              <motion.p
                className="text-lg sm:text-xl text-white/80 mb-6 leading-relaxed"
                initial={{ opacity: 0 }}
                animate={inView ? { opacity: 1 } : {}}
                transition={{ delay: 0.4 }}
              >
                I'm <strong>tharc</strong>, a creative product designer with a passion for crafting
                beautiful, user-centered digital experiences. With over 5 years
                of experience, I've worked with startups and established
                companies to bring their visions to life.
              </motion.p>
              <motion.p
                className="text-lg sm:text-xl text-white/80 mb-6 leading-relaxed"
                initial={{ opacity: 0 }}
                animate={inView ? { opacity: 1 } : {}}
                transition={{ delay: 0.5 }}
              >
                My approach combines research, creativity, and technical
                expertise to design interfaces that are not only visually
                stunning but also intuitive and accessible. I believe great
                design solves problems and creates meaningful connections
                between users and products.
              </motion.p>
              <motion.p
                className="text-lg sm:text-xl text-white/80 leading-relaxed"
                initial={{ opacity: 0 }}
                animate={inView ? { opacity: 1 } : {}}
                transition={{ delay: 0.6 }}
              >
                When I'm not designing, you'll find me exploring new design
                trends, attending design conferences, or sharing knowledge with
                the design community.
              </motion.p>
            </motion.div>

            {/* Profile Image with Parallax */}
            <motion.div
              variants={itemVariants}
              className="relative"
              initial={{ opacity: 0, scale: 0.9 }}
              animate={inView ? { opacity: 1, scale: 1 } : {}}
              transition={{ delay: 0.5, duration: 0.8 }}
            >
              <motion.div
                style={{ y: imageY }}
                className="relative aspect-square bg-gradient-to-br from-white/10 to-white/5 rounded-2xl p-4 sm:p-6 md:p-8 border border-white/10 backdrop-blur-sm overflow-hidden group"
                whileHover={{ 
                  scale: 1.05,
                  boxShadow: '0 20px 60px rgba(255, 255, 255, 0.2)',
                }}
                transition={{ type: 'spring', stiffness: 300, damping: 30 }}
              >
                <div className="absolute inset-0 bg-[linear-gradient(45deg,transparent_25%,rgba(255,255,255,0.05)_25%,rgba(255,255,255,0.05)_50%,transparent_50%,transparent_75%,rgba(255,255,255,0.05)_75%,rgba(255,255,255,0.05))] bg-[size:20px_20px] rounded-2xl" />
                <div className="relative h-full flex items-center justify-center">
                  <motion.div
                    style={{ scale: imageScale, rotate: imageRotate }}
                    className="relative w-full h-full max-w-xs max-h-xs rounded-full overflow-hidden border-4 border-white/20 shadow-2xl group-hover:border-white/40 transition-all duration-500"
                    whileHover={{ 
                      scale: 1.1,
                      rotate: 5,
                      boxShadow: '0 0 40px rgba(255, 255, 255, 0.4)',
                    }}
                    transition={{ type: 'spring', stiffness: 400, damping: 20 }}
                  >
                    <motion.img
                      src={profileImage}
                      alt="tharc - Product Designer"
                      className="w-full h-full object-cover"
                      whileHover={{ scale: 1.1 }}
                      transition={{ duration: 0.5 }}
                    />
                    <motion.div
                      className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent"
                      whileHover={{ opacity: 0 }}
                    />
                    <motion.div
                      className="absolute inset-0 bg-white/5 opacity-0 group-hover:opacity-100 transition-opacity duration-500"
                    />
                  </motion.div>
                </div>
              </motion.div>
            </motion.div>
          </div>

          {/* Stats */}
          <motion.div
            variants={containerVariants}
            initial="hidden"
            animate={inView ? 'visible' : 'hidden'}
            className="grid grid-cols-2 md:grid-cols-4 gap-8"
          >
            {stats.map((stat, index) => (
              <motion.div
                key={index}
                variants={itemVariants}
                className="text-center p-6 bg-white/5 rounded-xl border border-white/10 backdrop-blur-sm hover:bg-white/10 transition-colors relative overflow-hidden group"
                whileHover={{ 
                  scale: 1.1, 
                  y: -10,
                  boxShadow: '0 20px 40px rgba(255, 255, 255, 0.1)',
                }}
                transition={{ type: 'spring', stiffness: 300, damping: 20 }}
              >
                <motion.div
                  className="text-3xl sm:text-4xl md:text-5xl font-bold text-white mb-2 relative z-10"
                  initial={{ scale: 0 }}
                  animate={inView ? { scale: 1 } : {}}
                  transition={{ 
                    delay: 0.8 + index * 0.1,
                    type: 'spring',
                    stiffness: 200,
                    damping: 15,
                  }}
                >
                  {stat.number}
                </motion.div>
                <motion.div
                  className="text-sm sm:text-base text-white/60 relative z-10"
                  initial={{ opacity: 0 }}
                  animate={inView ? { opacity: 1 } : {}}
                  transition={{ delay: 1 + index * 0.1 }}
                >
                  {stat.label}
                </motion.div>
                <motion.div
                  className="absolute inset-0 bg-gradient-to-br from-white/10 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                />
              </motion.div>
            ))}
          </motion.div>
        </motion.div>
      </div>
    </section>
  );
};

export default About;
