import React, { useEffect, useRef } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import AnimatedBackground from '../components/AnimatedBackground';

const Hero = () => {
  const ref = useRef(null);
  const { scrollYProgress } = useScroll({
    target: ref,
    offset: ['start start', 'end start'],
  });

  const y = useTransform(scrollYProgress, [0, 1], ['0%', '50%']);
  const opacity = useTransform(scrollYProgress, [0, 0.5], [1, 0]);
  const scale = useTransform(scrollYProgress, [0, 1], [1, 0.8]);

  // Parallax layers with different speeds
  const backgroundY1 = useTransform(scrollYProgress, [0, 1], ['0%', '30%']);
  const backgroundY2 = useTransform(scrollYProgress, [0, 1], ['0%', '60%']);
  const backgroundY3 = useTransform(scrollYProgress, [0, 1], ['0%', '90%']);

  const smoothY = useSpring(y, { stiffness: 100, damping: 30 });

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.3,
        delayChildren: 0.2,
      },
    },
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 50 },
    visible: {
      opacity: 1,
      y: 0,
      transition: {
        duration: 0.8,
        ease: [0.6, -0.05, 0.01, 0.99],
      },
    },
  };

  return (
    <section
      id="home"
      ref={ref}
      className="relative min-h-screen flex items-center justify-center overflow-hidden bg-black"
    >
      {/* Animated Background Elements with Parallax */}
      <div className="absolute inset-0 overflow-hidden">
        <motion.div
          style={{ y: backgroundY1 }}
          className="absolute top-20 left-10 w-72 h-72 bg-white/5 rounded-full blur-3xl"
          animate={{
            x: [0, 100, 0],
            y: [0, 50, 0],
            scale: [1, 1.2, 1],
          }}
          transition={{
            duration: 20,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
        <motion.div
          style={{ y: backgroundY2 }}
          className="absolute bottom-20 right-10 w-96 h-96 bg-white/5 rounded-full blur-3xl"
          animate={{
            x: [0, -100, 0],
            y: [0, -50, 0],
            scale: [1, 1.3, 1],
          }}
          transition={{
            duration: 25,
            repeat: Infinity,
            ease: 'easeInOut',
          }}
        />
        <motion.div
          style={{ y: backgroundY3 }}
          className="absolute top-1/2 left-1/2 w-64 h-64 bg-white/3 rounded-full blur-3xl -translate-x-1/2 -translate-y-1/2"
          animate={{
            scale: [1, 1.5, 1],
            rotate: [0, 360],
          }}
          transition={{
            duration: 30,
            repeat: Infinity,
            ease: 'linear',
          }}
        />
      </div>

      {/* Grid Pattern Overlay with Parallax */}
      <motion.div
        style={{ y: backgroundY1 }}
        className="absolute inset-0 bg-[linear-gradient(rgba(255,255,255,0.03)_1px,transparent_1px),linear-gradient(90deg,rgba(255,255,255,0.03)_1px,transparent_1px)] bg-[size:50px_50px]"
      />

      {/* Animated Background Elements */}
      <AnimatedBackground variant="hero" />

      {/* Content */}
      <motion.div
        style={{ y: smoothY, opacity, scale }}
        variants={containerVariants}
        initial="hidden"
        animate="visible"
        className="relative z-10 text-center px-4 sm:px-6 lg:px-8 max-w-5xl mx-auto"
      >
        <motion.div variants={itemVariants} className="mb-8">
          <motion.span
            className="inline-block px-6 py-2.5 text-sm font-medium text-white/80 border border-white/20 rounded-full backdrop-blur-sm mb-6 tracking-wider"
            whileHover={{ scale: 1.05, borderColor: 'rgba(255,255,255,0.4)' }}
          >
            ✨ UI/UX Designer
          </motion.span>
        </motion.div>

        <motion.h1
          variants={itemVariants}
          className="text-6xl sm:text-7xl md:text-8xl lg:text-9xl font-extralight text-white mb-8 leading-[1.1] tracking-tight"
        >
          <motion.span 
            className="block font-light"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3, duration: 0.8 }}
          >
            I'm tharc
          </motion.span>
          <motion.span 
            className="block font-light bg-gradient-to-r from-white via-white/90 to-white/70 bg-clip-text text-transparent"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.4, duration: 0.8 }}
          >
            Product Designer
          </motion.span>
        </motion.h1>

        <motion.p
          variants={itemVariants}
          className="text-xl sm:text-2xl md:text-3xl text-white/60 mb-16 max-w-3xl mx-auto leading-relaxed font-light tracking-wide"
        >
          Crafting beautiful and intuitive digital experiences that blend
          <span className="text-white/80"> aesthetics </span>
          with
          <span className="text-white/80"> functionality </span>
        </motion.p>

        <motion.div
          variants={itemVariants}
          className="flex flex-col sm:flex-row gap-4 justify-center items-center"
        >
          <motion.a
            href="#projects"
            className="px-10 py-5 bg-white text-black font-medium rounded-full relative overflow-hidden group shadow-xl shadow-white/30 text-lg tracking-wide"
            whileHover={{ 
              scale: 1.05,
              boxShadow: '0 25px 50px rgba(255, 255, 255, 0.4)',
            }}
            whileTap={{ scale: 0.98 }}
            transition={{ type: 'spring', stiffness: 400, damping: 20 }}
          >
            {/* Default Black Text */}
            <span className="relative z-20 flex items-center gap-2 text-black group-hover:opacity-0 transition-opacity duration-300">
              View My Work
              <span className="text-xl text-black">→</span>
            </span>

            {/* Hover Black Background with White Text */}
            <motion.div
              className="absolute inset-0 bg-black flex items-center justify-center rounded-full"
              initial={{ x: '-100%' }}
              whileHover={{ x: 0 }}
              transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            >
              <span className="text-white flex items-center gap-2 relative z-30 opacity-0 group-hover:opacity-100 transition-opacity duration-300 delay-100">
                View My Work
                <span className="text-xl text-white">→</span>
              </span>
            </motion.div>

            {/* Shine Effect */}
            <motion.div
              className="absolute inset-0 bg-gradient-to-r from-transparent via-white/30 to-transparent pointer-events-none z-10"
              initial={{ x: '-200%' }}
              whileHover={{ x: '200%' }}
              transition={{ duration: 0.6 }}
            />
          </motion.a>

          <motion.a
            href="#contact"
            className="px-10 py-5 border-2 border-white/40 text-white font-medium rounded-full relative overflow-hidden group backdrop-blur-sm bg-white/5 text-lg tracking-wide"
            whileHover={{ 
              scale: 1.05,
              borderColor: 'rgba(255, 255, 255, 0.8)',
              boxShadow: '0 0 40px rgba(255, 255, 255, 0.2)',
              backgroundColor: 'rgba(255, 255, 255, 0.1)',
            }}
            whileTap={{ scale: 0.98 }}
            transition={{ type: 'spring', stiffness: 400, damping: 20 }}
          >
            <motion.span className="relative z-10 flex items-center gap-2">
              Get In Touch
              <span className="text-xl">✉</span>
            </motion.span>
            <motion.div
              className="absolute inset-0 bg-white/10 backdrop-blur-md"
              initial={{ scale: 0, borderRadius: '9999px' }}
              whileHover={{ scale: 1 }}
              transition={{ type: 'spring', stiffness: 300, damping: 25 }}
            />
          </motion.a>
        </motion.div>
      </motion.div>

      {/* Scroll Indicator */}
      <motion.div
        className="absolute bottom-10 left-1/2 transform -translate-x-1/2"
        animate={{ y: [0, 10, 0] }}
        transition={{ duration: 1.5, repeat: Infinity }}
      >
        <div className="w-6 h-10 border-2 border-white/50 rounded-full flex justify-center">
          <motion.div
            className="w-1 h-3 bg-white/50 rounded-full mt-2"
            animate={{ y: [0, 12, 0] }}
            transition={{ duration: 1.5, repeat: Infinity }}
          />
        </div>
      </motion.div>
    </section>
  );
};

export default Hero;
